import React, {useState, useEffect, useRef} from 'react';
import './App.css';
import axios from 'axios';

const App = () => {

  const codeAreaRef = useRef();
  const [code, setCode] = useState("");
  const [result, setResult] = useState("");
  const [selectedOption, setSelectedOption] = useState(options[0]);
  const options = [{label: "AST", value: "-a"}, {label: "SAST", value: "-s"}, {label: "LLVM IR", value: "-l"}, {label: "Tokens"}];

  const run = async () => {
    const res = await axios.post("http://localhost:5001/run", {content: code, language: "viz"});
    // const res = await axios.post("/run", {content: code, language: "viz"});
    setResult(res.data);
  }

  return (
    <div className="page">
      <h3 className="title">Viz Online</h3>
      <select
        value={selectedOption}
        onChange={e => setSelectedOption(e.target.value)}>
          <option key={o.value} value={o.value}>{o.label}</option>
      </select>
        <div className="container">
          <textarea className="textArea"
            rows="20"
            cols="75"
            value={code}
            ref={codeAreaRef}
            onChange={(e) => {
              setCode(e.target.value);
            }}
            onKeyDown={(e) => {
              if (e.key == 'Tab') {
                e.preventDefault();
                const { selectionStart, selectionEnd } = e.target;
                const newText = code.substring(0, selectionStart) + '    ' + code.substring(selectionEnd, code.length);
                codeAreaRef.current.focus();
                codeAreaRef.current.value = newText;
                codeAreaRef.current.setSelectionRange(selectionStart + 4, selectionEnd + 4);
                setCode(newText);
              }
            }}
          />
          <textarea className="textArea"
            rows="20"
            cols="75"
            value={result}
            disabled={true}
          />
        </div>
        <button className="run-btn" onClick={run}>Run</button>
    </div>
  );
}

export default App;
